/*
Function Name : onLoad()
Comments: This function will display variables of resource day2 action catalog on RITM as readonly.
Updated On: 22-11-2022
Updated By: Shivam Goyal
Update Comments : I have added the parameter for setDisplay function also handled the readonly part of the variables, by showing only relevant fields
*/
function onLoad() {
    g_form.setVariablesReadOnly(true);
    var catalog = g_form.getValue('cat_item');
    var response1 = g_form.getValue('mc_schema');
    var fieldsArr = JSON.parse(response1);
    var schema = fieldsArr.schema;
    var version = fieldsArr.versions;
    if (schema != '') {
        var parsed = JSON.parse(schema);
        if (parsed && parsed.model && parsed.model.schema) {
            var properties = parsed.model.schema;
            for (var key in properties) {
                var rgx = new RegExp('[^a-zA-Z0-9]', 'g');
                var name = key.replace(rgx, ' ').trim().replaceAll(' ', '_');
                if (properties[key].type.dataType.toString() == 'boolean') {
                    g_form.setDisplay(name + '_label__' + version, true);
                } else {
                    g_form.setDisplay(name + '__' + version, true);
                }

            }
        } else {
            g_form.setDisplay('reason', true);
        }
    } else {
        g_form.setDisplay('reason', true);
    }
    var gAjax = new GlideAjax('x_vmw_cloudservice.CSClientScriptBridge');
    gAjax.addParam('sysparm_name', 'getAllFieldsVersion');
    gAjax.addParam('sysparm_catalogid', catalog);
    gAjax.addParam('sysparm_version', version);
    gAjax.getXMLAnswer(callBack);

    function callBack(response) {
        var answer = response;
        var arr = answer.split(",");
        for (var item = 0; item < arr.length; item++) {
            g_form.setMandatory(arr[item], false);
            g_form.setDisplay(arr[item], false);
        }
    }
}
