function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var gAjax = new GlideAjax('x_vmw_cloudservice.CSClientScriptBridge');
    gAjax.addParam('sysparm_name', 'getEndpoint');
    gAjax.addParam('sysparm_deploymentid', newValue);
    gAjax.getXMLAnswer(callBack);

    function callBack(response) {
        var answer = response;
        var parsed = JSON.parse(answer);
        if (parsed.endpoint) {
            g_form.setValue('endpoint', parsed.endpoint);
            g_form.setVisible('endpoint', true);
        }
        if (parsed.project) {
            g_form.setValue('project', parsed.project);
            g_form.setVisible('project', true);
        }
		var resource =	g_form.getValue('resource');
		var gAjax1 = new GlideAjax('x_vmw_cloudservice.CSClientScriptBridge');
		gAjax1.addParam('sysparm_name', 'checkResource');
		gAjax1.addParam('sysparm_deploymentid', newValue);
		gAjax1.addParam('sysparm_resourceid', resource);	
		gAjax1.getXMLAnswer(callBack1);

		function callBack1(response1) {
			var answer1 = response1;
			if(answer1== 'false')
			g_form.clearValue('resource');
		}
    }
}
