/*
    Function Name : onLoad script.
    Comments: onLoad Script for displaying Custom Loader on VMware Aria Automation Catalog Item in Native UI.
    Updated On: 17 Sept 2021
    Updated By: Gayatri
    Update Comments : Getting the system property value by using GlideAjax which checks if the answer is coming true then the loader will come and if false then will not  come.
    */

function onLoad() {
    var ga = new GlideAjax('CSClientScriptBridge');
    ga.addParam('sysparm_name', 'getProperty');
    ga.getXML(result);
    function result(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
		var ans = answer.toLowerCase();

        if (ans == "true" || ans == true) {

         jslog("loader starts...");
            try {

                var loadingDialog2 = new GlideDialogWindow("x_vmw_cloudservice_Loader_Catalog", true, "500", "200");
                loadingDialog2.setTitle('');
                loadingDialog2.render();
                var interval = setInterval(function() {
                    jslog("loader interval is running... " + sessionStorage.getItem('catalog_script_counter'));

                    if (sessionStorage.getItem('catalog_script_counter') <= 0) {
                        loadingDialog2.destroy();
                        clearInterval(interval);
                        jslog('loader ends.');
                    }
                }, 500);
            } catch (e) {
                jslog("Custom Loader is not available in Service Portal");
            }
		} } 
}
