function onLoad() {
   //Type appropriate comment here, and begin script below

	var url = top.location.href;
	var deployId = new URLSearchParams(url).get("deploy_sys_id");
	var macId = new URLSearchParams(url).get("mac_sys_id");
// 	console.log(deployId);
// 	console.log(macId);

	if(macId == -1){
		g_form.setValue('deployment',deployId);
	}

}
