function onLoad() {	
    var gajax = new GlideAjax('x_vmw_cloudservice.FetchVersions');
    gajax.addParam('sysparm_name', 'isUserAuthorized');
    gajax.addParam('sysparm_userID', g_user.userID);
    gajax.getXMLAnswer(callBack);

    function callBack(response) {
        if (response != 'true') {
            alert('You are being redirected to VMware Aria Automation Cloud Login Page');
			top.window.open(response,'','height=500,width=800');
        }
    }
}
