/*
Function Name : onLoad()
Comments: This function will display variables of deployment day2 action catalog on RITM as readonly.
Updated On: 14-07-2022
Updated By: Zabiulla M
Update Comments : I have added the missing parameter to setDisplay function and also modified the logic to handle empty schema(preferebly in 'delete' deployment action)
updated by Sachin Gavhane on 25th Sep 2023 : for the bug VSNOW 3077 :[RITM]Extra field are visible on RITM for Delete Deployment Day-2 action.
*/
function onLoad() {
    g_form.setVariablesReadOnly(true);
    var catalog = g_form.getValue('cat_item');
    var response1 = g_form.getValue('dp_schema');
    var fieldsArr1 = JSON.parse(response1);
    var schema = fieldsArr1.schema;
    var version = fieldsArr1.versions;
    var nameArray = [];
    if (schema != '') {
        var parsed = JSON.parse(schema);
        if (parsed && parsed.model && parsed.model.schema && Object.keys(parsed.model.schema).length > 0) {
            var properties = parsed.model.schema;
            for (var key in properties) {
                var rgx = new RegExp('[^a-zA-Z0-9]', 'g');
                var name = key.replace(rgx, ' ').trim().replaceAll(' ', '_');
                nameArray.push(name + '__' + version);
                if (properties[key].type.dataType.toString() == 'boolean') {
                    g_form.setDisplay(name + '_label__' + version, true);
					nameArray.push(name + '_label__' + version);
                }
                g_form.setDisplay(name + '__' + version, true);
            }
        } else {
            g_form.setDisplay('reason', true);
        }
    } else {
        g_form.setDisplay('reason', true);
    }
    var gAjax = new GlideAjax('x_vmw_cloudservice.CSClientScriptBridge');
    gAjax.addParam('sysparm_name', 'getAllhiddenFields');
    gAjax.addParam('sysparm_catalogid', catalog);
    gAjax.addParam('sysparm_array', nameArray);
    gAjax.getXMLAnswer(callBack);

    function callBack(response) {
        var answer = response;
        var arr = answer.split(",");
        for (var item = 0; item < arr.length; item++) {
            g_form.setMandatory(arr[item], false);
            g_form.setDisplay(arr[item], false);
        }
    }
}
