(function executeRule(current, previous /*null when async*/ ) {
    try {
        CASLogger.debug('Inside BR:Disable/Enable OAuth');
        this.appUtil = new CASAppUtil();
        if (current.value == false || current.value == 'false') {
            var endpoint = new GlideRecord(this.appUtil.configuration.table.register_cas);
            endpoint.addQuery('active', true);
            endpoint.addQuery('use_oauth', true);
            endpoint.query();
            while (endpoint.next()) {
                endpoint.use_oauth = false;
                endpoint.update();
            }
        }
    } catch (e) {
        CASLogger.error("Exception caught inside BR:Disable/Enable OAuth" + e);
    }
})(current, previous);
