(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	try{
		CASLogger.debug('Inside CS BR to set number of iterations Business Rule');
		var appUtil = new CASAppUtil();
		var timer=gs.getProperty(appUtil.configuration.properties.requestStatusTimer);
		var noOfIterations=current.value/timer;                 //requestStatusTimer
		if(noOfIterations < 1){
			noOfIterations=1;
		}

		var sysId= gs.getProperty(appUtil.configuration.properties.Request_Iteration);
		var wf=new GlideRecord(appUtil.configuration.table.workflowActivity);
		wf.addQuery('sys_id',sysId);  
		wf.query();
		if(wf.next()){
			wf.vars.iterations=noOfIterations;
			wf.update();
		}
	}catch(e){
		CASLogger.error("Exception caught inside CS BR to set number of iterations Business Rule "+e);
	}


})(current, previous);
