(function executeRule(current, previous /*null when async*/) {
	var appUtil = new CASAppUtil();
	try
	{
		CASLogger.debug('Inside Create incident from RITM:Business Rule');
		// Add your code here
		var group = gs.getProperty(appUtil.configuration.properties.support_group_new);
		var value = gs.getProperty(appUtil.configuration.properties.incident_for_failure_catalogitems);

		if(value=='true')
		{
			var inc= new GlideRecordSecure(appUtil.configuration.table.incident);

			inc.initialize();
			inc.caller_id=current.request.requested_for;
			inc.category='inquiry';
			var grp=new GlideRecordSecure(appUtil.configuration.table.groups_table);
			grp.addEncodedQuery('name='+group);
			grp.query();
			while(grp.next())
			{
				var grp_sysid=grp.sys_id;
			}
			inc.assignment_group=grp_sysid;			
			inc.short_description='Error occurred while submitting the Requested item '+current.number;
			inc.insert();}
	}
	catch(e)
	{
		CASLogger.error("Exception caught Inside Create incident from RITM:Business Rule "+e);
	}


})(current, previous);
