(function executeRule(current, previous /*null when async*/ ) {

    try {
        CASLogger.debug('Inside BR:Aria module visibility');

        this.appUtil = new CASAppUtil();

        var nativeServicePortalModule = gs.getProperty(this.appUtil.configuration.properties.ServicePoratlUISysIds);
		var userCriteriaSysid = gs.getProperty(this.appUtil.configuration.properties.usercriteriaModuleSysid);


		if (current.value == false || current.value == 'false') {
            var grMod = new GlideRecord(this.appUtil.configuration.table.module);
            grMod.addEncodedQuery("sys_idIN" + nativeServicePortalModule);

            grMod.query();
            while (grMod.next()) {

                grMod.active = true;
                if (grMod.sys_id == userCriteriaSysid) {
                    grMod.active = false;
                }
                grMod.update();
            }
        }


        if (current.value == true || current.value == 'true') {
            var nativeUI = new GlideRecord(this.appUtil.configuration.table.module);
            nativeUI.addEncodedQuery("sys_idIN" + nativeServicePortalModule);
            nativeUI.query();
            while (nativeUI.next()) {

                nativeUI.active = false;
                if (nativeUI.sys_id == userCriteriaSysid) {
                    nativeUI.active = true;
                }
                nativeUI.update();
            }
        }
    } catch (e) {
        CASLogger.error("Exception caught inside BR: Aria module visibility" + e);
    }

})(current, previous);
