(function executeRule(current, previous /*null when async*/) {

	try{

		var appUtil = new CASAppUtil();
		var value = gs.getProperty(appUtil.configuration.properties.EndpointEmailNotification);

		if(value == 'true' && (current.active == true || previous.active == true)){


			//Handle the Automatic changing the API Token by System. 	

				CASLogger.debug("Inside BR Endpoint Email Notification for Update Operation");

				//get the Created By User's Email ID.
				var CreatedBy = new GlideRecordSecure(appUtil.configuration.table.users_table);
				CreatedBy.addEncodedQuery('user_name='+current.sys_created_by);
				CreatedBy.query();

				////get the Updates By User's Email ID.
				//var UpdatedBy = new GlideRecordSecure(appUtil.configuration.table.users_table);
				//UpdatedBy.addEncodedQuery('user_name='+current.sys_updated_by);
				//UpdatedBy.query();

				//assinging the email 
				if(CreatedBy.next()) var Creator_email = CreatedBy.email;
				//if(UpdatedBy.next()) var Updator_email = UpdatedBy.email;

				if(current.active != previous.active){
				//handle the Active/Deactive Scenario

					gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_ac', current, Creator_email);
					//if(Creator_email == Updator_email) gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_ac', current, Creator_email);
					//else gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_ac', current, Creator_email, Updator_email);

				}else{
				//handle the Other field updation Scenario

					gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_up', current, Creator_email);
					//if(Creator_email == Updator_email) gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_up', current, Creator_email);
					//else gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_up', current, Creator_email, Updator_email);

				}

		}

	}catch(e){
		CASLogger.error("Exception caught inside BR: Endpoint Updation Email Notification "+e);
	}

})(current, previous);
