(function executeRule(current, previous /*null when async*/ ) {

    try {
		CASLogger.debug('Inside  Attach Picture To Catalog.Business Rule: Inserting Picture to Picture Field on catalog item ');
        var gr_cat = new GlideRecord('x_vmw_cloudservice_cas_catalog_items');
        gr_cat.addEncodedQuery('sys_id=' + current.table_sys_id);
        gr_cat.query();
        if (gr_cat.next()) {
            gr_cat.picture = current.sys_id;
            gr_cat.update();
        }
    } catch (e) {
        CASLogger.error("Exception caught Inside Attach Picture To Catalog.Business Rule:" + e);
    }
})(current, previous);
