(function executeRule(current, previous /*null when async*/ ) {

    try {
        CASLogger.debug("Inside BR Resource Day2 action Visibility for making visibility false ");
        this.appUtil = new CASAppUtil();
        var grActions = new GlideRecord(this.appUtil.configuration.table.action);
        grActions.addQuery("machine", current.sys_id);
        grActions.query();
        while (grActions.next()) {
            grActions.valid = 'false';
            grActions.update();
        }
    } catch (e) {
        CASLogger.error('Exception caught inside BR of Resource Day2 action Visibility ' + e);
    }

})(current, previous);
