(function executeRule(current, previous /*null when async*/) {


	try{
		CASLogger.debug('Inside BR for request cancellation:Business Rule ');
		var appUtil=new CASAppUtil();
		var timer=gs.getProperty(appUtil.configuration.properties.timerToReTriggerRequest);
		timer  =  timer/60;
		var noOfIterations = current.value/timer;
		if(noOfIterations < 1){
			noOfIterations=1;
		}

        var sys_id=gs.getProperty(appUtil.configuration.properties.Request_Retrigger);
		var wf=new GlideRecordSecure(appUtil.configuration.table.workflowActivity);
		wf.addQuery('sys_id', sys_id);    //turnstile activity sys_id used in workflow  (through system property)     
		wf.query();   
		if(wf.next()){
			wf.vars.iterations=noOfIterations;
			wf.update();
		}
	}catch(e){
		CASLogger.error('Exception caught inside  BR for request cancellation: '+e);
	}

})(current, previous);
