(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	try
	{
		CASLogger.debug('Inside  deletionDescriptionRITM.Business Rule: Delete of description');
		var grReq=new GlideRecord('sc_req_item');
		grReq.addEncodedQuery('cat_item='+current.sys_id);
		grReq.query();
		while(grReq.next()){
			grReq.description='';
			grReq.update();
		}
	}
	catch(e)
	{
		CASLogger.error("Exception caught Inside deletionDescriptionRITM.Business Rule:"+e);
	}

})(current, previous);
