function onLoad() {
	var check = g_form.getValue('active');
	var name = g_form.getValue('name');
	if ((check == true || check == 'true') && name == '') {
		var ga = new GlideAjax('CSClientScriptBridge');
		ga.addParam('sysparm_name', 'checkDomainSeparation');
		ga.getXML(result);
		function result(response) {
			var answer = response.responseXML.documentElement.getAttribute('answer');
			if(answer == true || answer == 'true'){
				g_form.addInfoMessage("Domain separation plugins are activated on this instance. Please select the domain from domain picker to configure the endpoint.");
			}
		}
	}
}
