(function executeRule(current, previous /*null when async*/ ) {

    try {

        CASLogger.debug("Inside BR MakeRelationshipMachinesProject for Making Relationship with project");
        this.appUtil = new CASAppUtil();
        var projectGR = new GlideRecord(this.appUtil.configuration.table.project);
        projectGR.addQuery("project_id", current.project_id);
        projectGR.query();
        if (projectGR.next()) {
            current.project = projectGR.sys_id;
          //current.update();
        }
    } catch (e) {
        CASLogger.error("Exception Caught inside BR MakeRelationshipMachinesProject " + e);
    }

})(current, previous);
