(function executeRule(current, previous /*null when async*/ ) {
    try {

        CASLogger.debug("Inside BR MakeDeploymentRelationResource for making relationship with Resource");
        this.appUtil = new CASAppUtil();

        if (current.catalog_id != '') {
            var catItem = new GlideRecord(this.appUtil.configuration.table.catalogItem);
            catItem.addQuery("cas_uid", current.catalog_id);
            catItem.query();
            if (catItem.next()) {
                current.u_catalog_item = catItem.sys_id;
            }
        }

        var reqItem = new GlideRecord('sc_req_item');
        reqItem.addQuery('x_vmw_cloudservice_deployment_id', current.deployment_id);
        reqItem.query();
        if (reqItem.next()) {
            current.servicenow_owner = reqItem.request.requested_for;
        }
        if (current.u_project != previous.u_project) {
            if (previous.u_project != "") {
                var prev_Group = [];
                var deployment_Groups = [];
                var proj_Groups = [];
                var result = [];
                var finaArray = [];
                if (current.shared_groups == "") {
                    current.shared_groups = current.u_project.project_group;
                } else {
                    prev_Group = previous.u_project.project_group.split(",");
                    deployment_Groups = current.shared_groups.split(",");
                    proj_Groups = current.u_project.project_group.split(",");
                    for (var i = 0; i < deployment_Groups.length; i++) {
                        if (prev_Group.indexOf(deployment_Groups[i]) == -1) {
                            result.push(deployment_Groups[i]);
                        }
                    }
                    for (var k = 0; k < result.length; k++) {
                        if (proj_Groups.indexOf(result[k]) == -1) {
                            finaArray.push(result[k]);
                        }
                    }
                    current.shared_groups = proj_Groups + "," + finaArray;
                }

            } else {

                var grProject = new GlideRecord(this.appUtil.configuration.table.project);
                grProject.addQuery("project_id", current.project_id);
                grProject.query();
                if (grProject.next()) {
                    if (current.shared_groups != '') {
                        var groups = grProject.project_group.split(",");
                        var length = groups.length;
                        for (var i = 0; i < length; i++) {
                            if (current.shared_groups.lastIndexOf(groups[i]) == -1) {
                                current.shared_groups = current.shared_groups + ',' + groups[i];
                            }
                        }
                    } else {
                        current.shared_groups = grProject.project_group;

                    }
                }
            }


        }
    } catch (e) {
        CASLogger.error('Exception caught inside BR  of MakeDeploymentRelationResource ' + e);
    }

})(current, previous);
