(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var appUtil= new CASAppUtil();
	try
		{
		CASLogger.debug('Inside Display_Deployments_RITM :Notification email script');
	var gr = new GlideRecordSecure(appUtil.configuration.table.deployments);

	gr.addQuery('deployment_id',current.x_vmw_cloudservice_deployment_id);
	gr.query();
	if(gr.next())
		{
			//template.print('arr');
			template.print(gr.name +'<br/>');
		}
			else
				{
					template.print('&nbsp &nbsp &nbsp &nbsp &nbsp-');
				}
		}
	catch(e)
		{
			CASLogger.error('Exception caught inside Display_Deployments_RITM :Notification email script'+e);
		}

