(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
try{

		CASLogger.debug("Inside EmailScript : Cat_item_more_details Email Notification for Cat_item ");

        var cat_item=current.document_id.cat_item.getDisplayValue();
	if(cat_item=="")
		{
			template.print('&nbsp;&nbsp;-');
		}
	else{

		template.print(cat_item);
	}
	}catch(e){
		CASLogger.error("Exception caught inside EmailScript : Cat_item_more_details Cat_item Email Notification "+e);
	}

})(current, template, email, email_action, event);
