(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
try{

		CASLogger.debug("Inside EmailScript : Entitlement Email Notification for Machine_actions");
        var desc=current.description;

	//getValue('description');
	if(current.description=="")
		{
			template.print('&nbsp;&nbsp;-');
		}
	else{

		template.print(desc);
	}
	}catch(e){
		CASLogger.error("Exception caught inside EmailScript : Entitlment Machine_actions Email Notification "+e);
	}

})(current, template, email, email_action, event);
