var CASChangeScriptEntitlementDeployment = Class.create();
CASChangeScriptEntitlementDeployment.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {



HideMachine: function() {
		try {
			CASLogger.debug("Inside CASChangeScriptEntitlementDeployment: HideMachine");
			var appUtil = new CASAppUtil();
			var newValueString = this.getParameter('entitledDeploymentsnewValue');
			var machineValueString = this.getParameter('entitledDeploymentsmachineValue');
			var newValue = [];
			newValue = newValueString.split(',');
			CASLogger.debug('Inside CASChangeScriptEntitlementDeployment.HideMachine: NewValue is '+newValue);

			var machineSysIds = '';
			for (var i =0; i < newValue.length; i++) {
				var relationGR = new GlideRecord(appUtil.configuration.table.relation);
				relationGR.addQuery('parent',newValue[i]);
				relationGR.query();
				while(relationGR.next()) {
					machineSysIds += relationGR.child + ',';
					//CASLogger.debug('Inside while loop of CASChangeScriptEntitlementDeployment.HideMachine:---machineSysIds: ' + machineSysIds);

				}
			}
			CASLogger.debug('Inside CASChangeScriptEntitlementDeployment.HideMachine:---machineSysIds: ' + machineSysIds);
			var machinesInfo = machineValueString.split(',');



			for(var j=machinesInfo.length-1; j>=0;j--)                                             
				{
					if(machineSysIds.indexOf(machinesInfo[j])==-1)
						{
							machinesInfo.splice(j,1);
						}

				}
			CASLogger.debug('Inside CASChangeScriptEntitlementDeployment.HideMachine:---machinesInfo: ' + machinesInfo);
			return machinesInfo.toString();






		} catch (e) {
			CASLogger.debug('Inside Catch Block of CASChangeScriptEntitlementDeployment:HideMachine: Exception Block: newValue: '+ newValue+ 'machineSysIds: '+ machineSysIds + ' machinesInfo: ' + machinesInfo);
			CASLogger.error('Exception caught in CASChangeScriptEntitlementDeployment  : HideMachine :' + e + '. Error found'+ e.stack);
		}

	},

	type:'CASChangeScriptEntitlementDeployment'

});
