var AvoidParallelScheduleJobs = Class.create();
AvoidParallelScheduleJobs.prototype = {
	initialize: function() {
	},

	checkQueueEmpty : function(type,organization)
	{
		try
		{
			CASLogger.debug('Inside  AvoidParallelScheduleJobs.checkQueueEmpty: Type '+type + ' organization: ' + organization);
			var grqueue = new GlideRecordSecure('x_vmw_cloudservice_cas_queue');	
			var query='';
			if(type == 'Project Import')
			{  

				query = 'topicINStartImportProject,StartedImportProject,ParseProject,ReconcileImportProject^stateNOT INCompleted,Error^organization='+organization;



			}
			else if(type == 'Catalog Import')
			{


				query = 'topicINStartImportCatalog,ScheduledImportCatalog,PreImportCatalog,ImportCatalog,ReconcileImportCatalog^stateNOT INCompleted,Error^organization='+organization;


			}
			else if(type == 'CMDB Import')
			{


				query = 'topicINStartImportCMDB,IndivImportCMDB,createCMDB,preImportResource,parseResource,createResource,reconcileCMDB,startImportActionForDeployment,parseActionForDeployment,parseActionSchemaForDeployment,startImportActionForResource,parseActionForResource,parseActionSchemaForResource,makeActionSchemaCalls^stateNOT INCompleted,Error^organization='+organization;

			}
			else if( type == 'Check All Topics'){


				query= 'topicINactionForEndpoint,StartImportProject,StartedImportProject,ParseProject,ReconcileImportProject,StartImportCatalog,ScheduledImportCatalog,PreImportCatalog,ImportCatalog,ReconcileImportCatalog,StartImportCMDB,IndivImportCMDB,createCMDB,preImportResource,parseResource,createResource,reconcileCMDB,startImportActionForDeployment,parseActionForDeployment,parseActionSchemaForDeployment,startImportActionForResource,parseActionForResource,parseActionSchemaForResource,makeActionSchemaCalls^stateNOT INCompleted,Error^organization='+organization;


			}

			// CASLogger.debug("Inside AvoidParallelScheduleJobs.checkQueueEmpty:---query: " +  query);
			grqueue.addEncodedQuery(query);
			grqueue.query();
			if(grqueue.next())
			{	

				CASLogger.debug('Inside if block of grqueue.next(): AvoidParallelScheduleJobs.checkQueueEmpty');
				return false;
			}
			else
			{
				CASLogger.debug('Inside else block of grqueue.next(): AvoidParallelScheduleJobs.checkQueueEmpty');
				return true;
			}

		}
		catch(e)
		{
			CASLogger.debug('Inside Catch Block of AvoidParallelScheduleJobs.checkQueueEmpty: Exception Block:  type: ' + type);
			CASLogger.error('Exception caught inside  checkQueueEmpty'+ e + ". Error found" + e.stack);
		}
	},


	type: 'AvoidParallelScheduleJobs'
};
