var RetryFailedTopics = Class.create();
RetryFailedTopics.prototype = {

    initialize: function() {
       this.appUtil = new CASAppUtil();
    },

    createRetryTopic: function(current) {
        var gr = new GlideRecord("x_vmw_cloudservice_cas_queue");
        gr.initialize();
        gr.response = current.response;
        gr.request = current.request;
        gr.organization = current.organization;
        gr.topic = 'retry_' + current.topic;
        gr.retry_count = gs.getProperty('x_vmw_cloudservice.topic_retry_count');
		if (current.day_2_record != "") {
            gr.day_2_record = current.day_2_record;
        }
        gr.state = 'Error';
        gr.insert();
    },

    //creating incidents for failed topics
    createIncidentForFailedTopic: function(current) {

		var value = gs.getProperty(this.appUtil.configuration.properties.incident_for_failed_topics);

		if(value=='true')
		{
        var group = gs.getProperty(this.appUtil.configuration.properties.support_group_new);
        var grp_sysid = '';
        var grp = new GlideRecordSecure(this.appUtil.configuration.table.groups_table);
        grp.addEncodedQuery('name=' + group);
        grp.query();
        while (grp.next()) {
            grp_sysid = grp.sys_id;
        }

        var grIncident = new GlideRecord("incident");
        grIncident.initialize();
        grIncident.short_description = 'VMWare ITSM - Retried topic: ' + '"' + current.topic + '"' + ' has been failed, please review it.';
        grIncident.description = 'Retried topic: ' + current.topic + ' has failed upon multiple retries. Please check the error logs to find out what went wrong.\n\n Here are the details about the failed topic: \n Request: ' + current.request + '\n Response: ' + current.response + '\n State: ' + current.state + ' ' + '\n Endpoint: ' + current.organization.getDisplayValue() + '\n RetryCount: ' + current.retry_count;
        grIncident.priority = '1';
        grIncident.assignment_group = grp_sysid;
        grIncident.insert();
		}
    },


    type: 'RetryFailedTopics'
};
