var daoUtil = new CASDaoUtil();
var avoidParallel = new AvoidParallelScheduleJobs();

var check = avoidParallel.checkQueueEmpty('Check All Topics', current.sys_id);
if (check == true) {

    daoUtil.insert("actionForEndpoint", 'delete : ' + current.name, 'delete', current.sys_id, 'Processing');
    gs.addInfoMessage("Deletion of the endpoint is in-progress. Please wait until the 'actionForEndpoint' job queue topic is completed. ");

} else {
    gs.addErrorMessage('Job Schedules are already running for this Endpoint. Please wait until the job Schedules are completed and then proceed with the endpoint deletion.');
}
