(function() {

    var appUtil = new CASAppUtil();
    var x = new CASDaoUtil();
    try {
        var configurationChecks = gs.getProperty(appUtil.configuration.properties.configurationCheck);
        if (configurationChecks == 'true') {
            CASLogger.debug('Inside Import Resources and CMDB Schedule Job');
            x.ScheduleJobForCMDB('NormalSync');
            x.checkThreadLimit();

        }
    } catch (e) {
        CASLogger.error("Exception caught inside Import Resources and CMDB" + e);
    }


})();
