(function() {

    var appUtil = new CASAppUtil();
    var daoUtil = new CASDaoUtil();
    try {
        var configurationChecks = gs.getProperty(appUtil.configuration.properties.configurationCheck);
        if (configurationChecks == 'true') {
            CASLogger.debug('Inside Reconcile Actions Schedule Job');
            daoUtil.insert("reconcileActions", "", "", "");
			daoUtil.insert("reconcileActionsDep", "", "", "");
            daoUtil.checkThreadLimit();

        }
    } catch (e) {
        CASLogger.error("Exception caught inside Reconcile Actions" + e);
    }


})();
